mapper.select:
SELECT RE_SEQ, USER_SEQ, TO_CHAR(RE_REGDATE,'YY-MM-DD HH24:MI:SS') RE_REGDATE , RE_CONTENT, RE_TEACHER, RE_STAR,FILEOX
	FROM REVIEWBOARD
		WHERE RE_SEQ =#{re_seq}

mapper.update:
UPDATE REVIEWBOARD 
SET  RE_CONTENT=#{re_content},RE_STAR=#{re_star}
WHERE RE_SEQ =#{re_seq} AND RE_REPLY ='N'

UPDATE REVIEWBOARD 
	SET RE_REPLY ='Y' 
	WHERE RE_SEQ =#{re_seq}

mapper.select:
SELECT FILEOX
	FROM GONGO g 
	WHERE GONGGO_SEQ=#{grade_seq}

SELECT FILE_SEQ, FILEBOARD, BOARD_SEQ, FILENAME, FILEREALNAME, FILEURL, FILEDATE, FILETYPE
	FROM FILEBOARD 
	WHERE FILEBOARD = 4000 AND BOARD_SEQ = #{grade_seq}
